package com.string;

public class StringConcat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s1="Hi";
		String s2="Rutu";
		String s3="Raj";
		
		String s4=s1.concat(s2);
		System.out.println(s4);
		
		String s5=s1.concat(s3);
		System.out.println(s5);
		
		 s1=s1.concat(s2);
		System.out.println(s1);

	}

}
